  return (
    <button 
      className="square"
      onClick={handleClick}
    >
      {value}
    </button>
  ) 
}

export default function Board() {
  return (
  <>
    <div className="board-row">
      <Square value={value} />
      <Square />
      <Square />
    </div>
    <div className="board-row">
      <Square />
      <Square />
      <Square />
    </div>
    <div className="board-row">
      <Square />
      <Square />
      <Square />
    </div>
  </>
  )
}
